"use strict";const upButton=document.querySelector(".up-button");upButton.addEventListener("click",(function(){window.scrollTo({left:0,top:0,behavior:"smooth"})}));const observer=new IntersectionObserver((e=>{e.forEach((e=>{e.isIntersecting?upButton.classList.remove("up-button--visible"):upButton.classList.add("up-button--visible")}))}));observer.observe(document.body);const appearingSections=document.querySelectorAll(".appearing-section"),appearanceObserver=new IntersectionObserver((function(e,t){e.forEach((e=>{e.isIntersecting&&(e.target.classList.remove("appearing-section--hide"),t.unobserve(e.target))}))}),{root:null,threshold:.2});appearingSections.forEach((function(e){e.classList.add("appearing-section--hide"),appearanceObserver.observe(e)}));const slides=document.querySelector(".slider__list"),slidesSum=slides.children.length,nextSlideBtn=document.querySelector(".slider__button--next"),previousSlideBtn=document.querySelector(".slider__button--prev");let currentSlide=0;function moveToSlide(e){slides.style.transform=`translateX(${100*-e}%)`}moveToSlide(0),nextSlideBtn.addEventListener("click",(function(){currentSlide===slidesSum-1?currentSlide=0:currentSlide++,moveToSlide(currentSlide)})),previousSlideBtn.addEventListener("click",(function(){0===currentSlide?currentSlide=slidesSum-1:currentSlide--,moveToSlide(currentSlide)}));